##CRIANDO SCHEMA - banco de dados
CREATE SCHEMA `dw` DEFAULT CHARACTER SET utf8 ;

##CRIANDO REPOSITÃ“RIOS DE DADOS - tabelas
CREATE TABLE `dw`.`Orders` (
    order_id INT NOT NULL AUTO_INCREMENT,
    product_id INT NOT NULL,
    customer_id INT NOT NULL,
    order_date DATE,
    quantity INT NOT NULL,
    price DOUBLE NOT NULL,
    discount DOUBLE,
    net_price DOUBLE NOT NULL,
    PRIMARY KEY (order_id),
    FOREIGN KEY (product_id) REFERENCES Products(product_id),
    FOREIGN KEY (customer_id) REFERENCES Customers(customer_id)
) ENGINE=INNODB DEFAULT CHARSET=utf8;

CREATE TABLE `dw`.`Products` (
    product_id INT NOT NULL,
    description VARCHAR(255) NOT NULL,
    stock_quantity INT NOT NULL,
    unit_price DOUBLE NOT NULL,
    provider VARCHAR(255),
    PRIMARY KEY (product_id)
) ENGINE=INNODB DEFAULT CHARSET=utf8;

CREATE TABLE `dw`.`Customers` (
    customer_id INT NOT NULL,
    name VARCHAR(255) NOT NULL,
    email VARCHAR(255) NOT NULL,
    adress VARCHAR(255) NOT NULL,
    birth_date DATE,
    user VARCHAR(20),
    password VARCHAR(20),
    status VARCHAR(10),
    PRIMARY KEY (customer_id)
) ENGINE=INNODB DEFAULT CHARSET=utf8;

